function mascaraCPF(e){e.addEventListener("input",function(){let t=e.value.replace(/\D/g,"");t.length>11&&(t=t.slice(0,11));t=t.replace(/(\d{3})(\d)/,"$1.$2");t=t.replace(/(\d{3})(\d)/,"$1.$2");t=t.replace(/(\d{3})(\d{1,2})$/,"$1-$2");e.value=t})}function mascaraTelefone(e){e.addEventListener("input",function(){let t=e.value.replace(/\D/g,"");t.length>11&&(t=t.slice(0,11));t=t.replace(/^(\d{2})(\d)/,"($1) $2");t=t.replace(/(\d{5})(\d)/,"$1-$2");e.value=t})}function mascaraCEP(e){e.addEventListener("input",function(){let t=e.value.replace(/\D/g,"");t.length>8&&(t=t.slice(0,8));t=t.replace(/(\d{5})(\d)/,"$1-$2");e.value=t})}document.addEventListener("DOMContentLoaded",()=>{const e=document.getElementById("cpf"),t=document.getElementById("telefone"),n=document.getElementById("cep"),a=document.querySelector(".hamburger"),c=document.querySelector("nav ul");e&&mascaraCPF(e);t&&mascaraTelefone(t);n&&mascaraCEP(n);a&&c&&a.addEventListener("click",()=>{c.classList.toggle("active")})});
