import{templates}from"./template.min.js";function loadTemplate(e){const t=document.getElementById("content");t.innerHTML=templates[e]?templates[e]:"<h2>Página não encontrada</h2>"}window.addEventListener("hashchange",()=>{const e=window.location.hash.replace("#","")||"inicio";loadTemplate(e)});document.addEventListener("DOMContentLoaded",()=>{const e=window.location.hash.replace("#","")||"inicio";loadTemplate(e)});
